$nav_reveal_button.on('click keydown', function(e) {
	if ((e.type == 'click') || (e.keyCode == 32)) {
		e.preventDefault();

		if ($(this).hasClass('is-active')) {
			closeNav();
			this.blur(); // Remove the focus from the button so :focus styles don't show on touch screens

		} else {
			openNav();
		}
	}
});


